

CREATE TABLE campaign(     campaignID TEXT,     clientID TEXT,     name TEXT,     status TEXT,     type TEXT,     layoutOrientation TEXT,     layoutResolution TEXT,     PRIMARY KEY(campaignID,clientID)     ); CREATE TABLE campaign_channel(     campaignID TEXT,     channelID TEXT,     channelTime TIME,     duration INTEGER,     fill TEXT,     height INTEGER,     left INTEGER,     mediaCount INTEGER,     top INTEGER,     volume TEXT,     width INTEGER,     PRIMARY KEY(campaignID,channelID)     ); CREATE TABLE player(     registrationID TEXT PRIMARY KEY,     deviceID TEXT,     clientID TEXT,     volume INTEGER,     isRegistered INTEGER DEFAULT 0,     orientation TEXT); CREATE TABLE asset(     assetID TEXT,     originalName TEXT,     type TEXT,     downloadUrl TEXT,     local_url TEXT,     vimeoId INTEGER,     sequence INETEGR,     PRIMARY KEY(assetID)     ); CREATE TABLE scheduled_asset(     assetID TEXT,     campaignID TEXT,     channelID TEXT,     scheduleID TEXT,     duration INTEGER,     animation TEXT,     sequence INT,     PRIMARY KEY(assetID,campaignID,channelID,scheduleID,sequence) ); CREATE TABLE proof_of_play(     id INTEGER PRIMARY KEY AUTOINCREMENT,     campaignID TEXT,     scheduleID TEXT,     assetID TEXT,     playDuration INTEGER,     createdDate DATE     ); CREATE TABLE schedule(     scheduleID TEXT,     campaignID TEXT,     startDateTime DATETIME,     endDateTime DATETIME,     isSync INTEGER DEFAULT 0,     PRIMARY KEY(scheduleID,campaignID,startDateTime,endDateTime)     ); CREATE TABLE eventlog(     id INTEGER PRIMARY KEY AUTOINCREMENT,     event TEXT,     loggedOn DATE DEFAULT(datetime('now','localtime')),     rawResponse TEXT     );

